mapper.select:
select
id, email, nickname, avatar, intro, web_site, is_disable, create_time, update_time
from tb_user_info


        and id = #{id}


        and email = #{email}


        and nickname = #{nickname}


        and avatar = #{avatar}


        and intro = #{intro}


        and web_site = #{webSite}


        and is_disable = #{isDisable}


        and create_time = #{createTime}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from tb_user_info


        and id = #{id}


        and email = #{email}


        and nickname = #{nickname}


        and avatar = #{avatar}


        and intro = #{intro}


        and web_site = #{webSite}


        and is_disable = #{isDisable}


        and create_time = #{createTime}


        and update_time = #{updateTime}
